INSERT INTO `roles_personal` (`id`, `codigo`, `nombre`, `descripcion`, `activo`, `fecha_creacion`) VALUES
(1,	'jefe_planta',	'Jefe de Planta',	'Responsable general de la operación de la planta',	1,	'2025-06-19 15:38:34'),
(2,	'laboratorista',	'Laboratorista',	'Encargado del control de calidad y pruebas de laboratorio',	1,	'2025-06-19 15:38:34'),
(3,	'operador_camion',	'Operador de Camión Revolvedor',	'Operador de vehículos de transporte de concreto',	1,	'2025-06-19 15:38:34'),
(4,	'operador_bombas',	'Operador de Bombas de Concreto',	'Operador de equipos de bombeo de concreto',	1,	'2025-06-19 15:38:34');
INSERT INTO `secciones_evaluacion` (`id`, `tipo_evaluacion_id`, `rol_personal_id`, `nombre`, `descripcion`, `orden`, `activo`, `fecha_creacion`) VALUES
(1001,	1,	1,	'Conocimiento técnico y operativo',	'Evaluación de conocimientos técnicos relacionados con los procesos productivos, equipos y tecnologías utilizadas en la planta. Incluye comprensión de especificaciones técnicas, procedimientos operativos y normativas industriales.',	1,	1,	'2025-06-19 16:32:53'),
(1002,	1,	1,	'Gestión de la producción',	'Capacidad para planificar, organizar y controlar los procesos productivos. Incluye optimización de recursos, programación de producción, cumplimiento de objetivos y eficiencia operacional.',	2,	1,	'2025-06-19 16:32:53'),
(1003,	1,	1,	'Mantenimiento del equipo',	'Gestión del mantenimiento preventivo y correctivo de equipos y maquinaria. Incluye planificación de mantenimientos, control de inventarios de repuestos y coordinación con equipos técnicos.',	3,	1,	'2025-06-19 16:32:53'),
(1004,	1,	1,	'Gestión del personal',	'Habilidades de liderazgo, supervisión y desarrollo del equipo de trabajo. Incluye motivación, capacitación, evaluación del desempeño y resolución de conflictos laborales.',	4,	1,	'2025-06-19 16:32:53'),
(1005,	1,	1,	'Seguridad y cumplimiento normativo',	'Implementación y supervisión de políticas de seguridad industrial, cumplimiento de normativas ambientales y laborales. Incluye prevención de accidentes y gestión de riesgos.',	5,	1,	'2025-06-19 16:32:53'),
(1006,	1,	1,	'Control de calidad',	'Supervisión y control de la calidad del producto final. Incluye implementación de sistemas de calidad, control de especificaciones y mejora continua de procesos.',	6,	1,	'2025-06-19 16:32:53'),
(1007,	1,	1,	'Documentación y control administrativo',	'Gestión de la documentación técnica y administrativa de la planta. Incluye reportes de producción, registros de control, documentación de procesos y archivo de información.',	7,	1,	'2025-06-19 16:32:53'),
(1008,	1,	1,	'Mejora continua y enfoque a resultados',	'Implementación de metodologías de mejora continua, análisis de indicadores de desempeño y búsqueda constante de optimización en procesos y resultados.',	8,	1,	'2025-06-19 16:32:53'),
(1009,	1,	1,	'Coordinación con logística y clientes',	'Coordinación con áreas de logística para el cumplimiento de entregas, comunicación con clientes y resolución de requerimientos especiales de producción.',	9,	1,	'2025-06-19 16:32:53'),
(1010,	1,	1,	'Resolución de problemas',	'Capacidad para identificar, analizar y resolver problemas operativos de manera eficiente. Incluye toma de decisiones bajo presión y implementación de soluciones efectivas.',	10,	1,	'2025-06-19 16:32:53');
INSERT INTO `tipos_evaluacion` (`id`, `codigo`, `nombre`, `descripcion`, `activo`, `fecha_creacion`) VALUES
(1,	'personal',	'Evaluación de Personal',	'Evaluación de competencias y conocimientos del personal',	1,	'2025-06-19 15:38:34'),
(2,	'equipo',	'Evaluación de Equipo',	'Evaluación del estado y funcionamiento de equipos',	1,	'2025-06-19 15:38:34'),
(3,	'operacion',	'Evaluación de Operación',	'Evaluación de procesos operativos y procedimientos',	1,	'2025-06-19 15:38:34');
