  PRIMARY KEY (`id`),
  UNIQUE KEY `email` (`email`),
  KEY `idx_email` (`email`),
  KEY `idx_nombre` (`nombre`),
  KEY `idx_usuarios_permiso` (`permiso`),
  KEY `idx_usuarios_email_permiso` (`email`,`permiso`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

